mapper.select:
select
  medicine_detail_id, medicine_detail_valid, medicine_detail_components, medicine_detail_production_date, medicine_detail_production_address, medicine_id
from medicine_detail


        and medicine_detail_id = #{medicineDetailId}


        and medicine_detail_valid = #{medicineDetailValid}


        and medicine_detail_components = #{medicineDetailComponents}


        and medicine_detail_production_date = #{medicineDetailProductionDate}


        and medicine_detail_production_address = #{medicineDetailProductionAddress}


        and medicine_id = #{medicineId}
