mapper.select:
select
  power_id, power_login, power_update, power_detele, position_id, employee_detail_status
from power


        and power_id = #{powerId}


        and power_login = #{powerLogin}


        and power_update = #{powerUpdate}


        and power_detele = #{powerDetele}


        and position_id = #{positionId}


        and employee_detail_status = #{employeeDetailStatus}
