mapper.select:
select
  purchase_id, purchase_entry_time, purchase_entry_price, purchase_provider, medicine_id
from purchase


        and purchase_id = #{purchaseId}


        and purchase_entry_time = #{purchaseEntryTime}


        and purchase_entry_price = #{purchaseEntryPrice}


        and purchase_provider = #{purchaseProvider}


        and medicine_id = #{medicineId}
